import React, { useState } from "react";
import Box from "@mui/material/Box";
import { ReactComponent as FileAccesIcon } from "../../../../../Assets/attach_file.svg";
import styles from "./ChatFileAcces.module.css";

const ChatFileAcces = () => {
  const [showOptions, setShowOptions] = useState(false);

  const handleFileOptionClick = (option) => {
    // Handle the selected file option here (image, video, document)
    console.log("Selected file option:", option);
    // Close the options box
    setShowOptions(false);
  };

  const closeOptions = () => {
    setShowOptions(false);
  };

  return (
    <Box
      sx={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "40px",
        position: "relative",
      }}
    >
      <FileAccesIcon
        className={styles.FileAccesIcon}
        onClick={() => setShowOptions(!showOptions)}
      />
      {showOptions && (
        <>
          <Box
            sx={{
              position: "absolute",
              bottom: "50px",
              left: 0,
              backgroundColor: "blue",
              borderRadius: "8px",
              boxShadow: "0px 2px 4px rgba(0, 0, 0, 0.1)",
              zIndex: 100,
              padding: "8px",
            }}
          >
            <div onClick={() => handleFileOptionClick("image")}>
              <span style={{ marginRight: "8px" }}>+</span>Image
            </div>
            <div onClick={() => handleFileOptionClick("video")}>
              <span style={{ marginRight: "8px" }}>+</span>Video
            </div>
            <div onClick={() => handleFileOptionClick("document")}>
              <span style={{ marginRight: "8px" }}>+</span>Document
            </div>
          </Box>

        </>
      )}
    </Box>
  );
};

export default ChatFileAcces;
