import React from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import ChatEmoji from "./ChatEmoji/ChatEmoji";
import ChatFileAcces from "./ChatFileAcces/ChatFileAcces";
import ChatMic from "./ChatMic/ChatMic";

const ChatInput = () => {
  return (
    <Box
      component="div"
      sx={{
        bgcolor: "grey",
        height: "100%",
        width: "100%",
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
      }}
    >
      <ChatEmoji />
      <ChatFileAcces />
      <TextField
        variant="outlined"
        placeholder="Type a message..."
        sx={{
          flexGrow: 1,
          fontSize: "clamp(14px, 2vw, 18px)",
          bgcolor: "#00000026",
          height: "40px", // Set the height for the TextField
          borderRadius: "50px",
          "& .MuiOutlinedInput-root": {
            padding: "0", // Reset padding if needed
            height: "40px", // Ensure root element takes 40px height
            "& .MuiOutlinedInput-input": {
              padding: "8px 20px", // Adjust padding to fit the height
              height: "24px", // Adjust the input element height
              boxSizing: "border-box", // Ensure padding is included in height calculation
            },
          },
          "& .MuiOutlinedInput-notchedOutline": {
            border: "none",
          },
          "&:focus": {
            outline: "none",
          },
          "&:focu": {
            outline: "none",
          },

        }}
      />

      <ChatMic />
    </Box>
  );
};

export default ChatInput;
