import React from "react";
import Box from "@mui/material/Box";
import useMediaQuery from '@mui/material/useMediaQuery';
import { useTheme } from '@mui/material/styles';

const SentMessage = () => {

    const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm')); // Adjust breakpoint for mobile devices
  const isTablet = useMediaQuery(theme.breakpoints.between('sm', 'md')); // Adjust breakpoint for tablets
  const isLaptop = useMediaQuery(theme.breakpoints.up('md')); // Adjust breakpoint for laptops


  return (
    <Box
    sx={{
        display: "inline-block", 
        bgcolor: "#dc6d36",
        maxWidth: "70%", // Set maximum width to 80%
        textAlign: "left", // Align text to the left
        p: "8px", // Add padding for better readability
        maxHeight: "100%",
        fontSize: isMobile ? "0.876" : isTablet ? "16px" : isLaptop ? "18px" : "inherit", // Adjust font size based on device
      }}
    >
        hey how are you bro
    </Box>
  );
};

export default SentMessage;
