import React from 'react'
import Box from '@mui/material/Box';
import {ReactComponent as EmojiIcon} from "../../../../../Assets/emoji_icon.svg"

function ChatEmoji() {
  return (
    <Box
    sx={{
      bgcolor: "blue",

      padding: {
        xs: "0px 20px",
        lg: "0px 40px", // Padding top and bottom is 0px, padding left and right is 40px for larger devices
      },
      "& .MuiSvgIcon-root": {
        fill: "black",
        fontSize: [
          "calc(10px + 1vw)", // Smallest size based on viewport width
          "24px", // Medium size for tablets
          "32px", // Large size for desktops
        ],
      },
      "& .MuiSvgIcon-root path": {
        fill: "black", // Set the fill color of the path elements to black
      },
    }}
  >
      <EmojiIcon/>
    </Box>
  )
}

export default ChatEmoji
