import React, { useState } from "react";
import Box from "@mui/material/Box";
import { ReactComponent as FileAccesIcon } from "../../../../../Assets/attach_file.svg";
import styles from "./ChatFileAcces.module.css";
import { useDropzone } from "react-dropzone";

const ChatFileAcces = () => {
  const [showOptions, setShowOptions] = useState(false);

  const handleFileOptionClick = (option) => {
    setShowOptions(false);
  };

  const onDrop = (acceptedFiles) => {
    // Handle the dropped files here
    console.log("Accepted files:", acceptedFiles);
  };

  const { getRootProps, getInputProps } = useDropzone({ onDrop });

  return (
    <Box
      sx={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "40px",
        position: "relative",
        "&:hover": {
          cursor: "pointer",
        },
      }}
    >
      <FileAccesIcon
        className={styles.FileAccesIcon}
        onClick={() => setShowOptions(!showOptions)}
      />
      {showOptions && (
        <>
          <Box
            sx={{
              position: "absolute",
              bottom: "52px",
              left: 0,
              backgroundColor: "white",
              borderRadius: "8px",
              boxShadow: "0px 2px 4px rgba(0, 0, 0, 0.1)",
              zIndex: 100,
              padding: "8px",
            }}
          >
            <div>
              <span onClick={() => handleFileOptionClick("image")} style={{ marginRight: "8px" }}>+</span>Image
            </div>
            <div>
              <span onClick={() => handleFileOptionClick("video")} style={{ marginRight: "8px" }}>+</span>Video
            </div>
            <div>
              <span style={{ marginRight: "8px" }}>+</span>Document
            </div>
          </Box>
        </>
      )}
      <div {...getRootProps()} style={{ display: "none" }}>
        <input {...getInputProps()} />
      </div>
    </Box>
  );
};

export default ChatFileAcces;
