import React from 'react'
import Box from '@mui/material/Box';
import {ReactComponent as EmojiIcon} from "../../../../../Assets/emoji_icon.svg"

function ChatEmoji() {
  return (
    <Box
      sx={{
        bgcolor: "blue",
        padding: {
          xs: "0px 20px",
          lg: "0px 40px", // Padding top and bottom is 0px, padding left and right is 40px for larger devices
        },
      }}
    >
      <EmojiIcon
        sx={{
          "& path": {
            fill: "black", // Set the fill color of the path elements to black
          },
          width: {
            xs: "calc(10px + 1vw)", // Smallest size based on viewport width
            md: "24px", // Medium size for tablets
            lg: "32px", // Large size for desktops
          },
          bgcolor:"red"
        }}
      />
    </Box>
  );
}

export default ChatEmoji
