import React, { useState, useEffect, useRef } from "react";
import Box from "@mui/material/Box";
import { ReactComponent as FileAccesIcon } from "../../../../../Assets/attach_file.svg";
import styles from "./ChatFileAcces.module.css";

const ChatFileAcces = () => {
  const [showOptions, setShowOptions] = useState(false);
  const optionsRef = useRef(null);

  const handleFileOptionClick = (option) => {
    // Handle the selected file option here (image, video, document)
    console.log("Selected file option:", option);
    // Close the options box
    setShowOptions(false);
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (optionsRef.current && !optionsRef.current.contains(event.target)) {
        setShowOptions(false); // Close the options box if clicked outside
      }
    };

    // Add event listener to handle clicks outside the options box
    document.addEventListener("click", handleClickOutside);

    return () => {
      // Cleanup: Remove event listener when component unmounts
      document.removeEventListener("click", handleClickOutside);
    };
  }, []);

  return (
    <Box
      sx={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "40px",
        position: "relative",
      }}
    >
      <FileAccesIcon
        className={styles.FileAccesIcon}
        onClick={() => setShowOptions(!showOptions)}
      />
      {showOptions && (
        <Box
          ref={optionsRef}
          sx={{
            position: "absolute",
            bottom: "50px",
            left: 0,
            backgroundColor: "white",
            borderRadius: "8px",
            boxShadow: "0px 2px 4px rgba(0, 0, 0, 0.1)",
            zIndex: 100,
            padding: "8px",
          }}
        >
          <div onClick={() => handleFileOptionClick("image")}>Image</div>
          <div onClick={() => handleFileOptionClick("video")}>Video</div>
          <div onClick={() => handleFileOptionClick("document")}>Document</div>
        </Box>
      )}
    </Box>
  );
};

export default ChatFileAcces;
