import React, { useState } from 'react';
import Box from '@mui/material/Box';
import { Picker } from 'emoji-mart';
import { ReactComponent as EmojiIcon } from "../../../../../Assets/emoji_icon.svg";
import styles from "./ChatEmoji.module.css";

function ChatEmoji() {
  const [showPicker, setShowPicker] = useState(false);
  const [chosenEmoji, setChosenEmoji] = useState(null);

  const handleEmojiSelect = (emoji) => {
    setChosenEmoji(emoji);
    // You can handle the selected emoji here, for example, add it to your message input
    console.log('Selected emoji:', emoji);
  };

  return (
    <Box
      sx={{
        position: "relative",
      }}
    >
      <Box
        sx={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          height: "40px",
          cursor: "pointer",
        }}
        onClick={() => setShowPicker(!showPicker)}
      >
        <EmojiIcon className={styles.chatemoji} />
      </Box>
      {showPicker && (
        <Box
          sx={{
            position: "absolute",
            bottom: "50px", // Adjust as needed
            right: "0",
            zIndex: "100", // Ensure the picker appears above other content
          }}
        >
          <Picker
            onSelect={handleEmojiSelect}
            showPreview={false} // Disable emoji preview
            showSkinTones={false} // Disable skin tone selector
          />
        </Box>
      )}
    </Box>
  );
}

export default ChatEmoji;
